	}
    document.documentElement.setAttribute('data-theme', currentTheme);
    if (currentTheme === 'dark') {
        toggleSwitch.checked = true;
    }
}

function switchTheme(e) {
    if (e.target.checked) {
        document.documentElement.setAttribute('data-theme', 'dark');
        localStorage.setItem('theme', 'dark');document.body.style.backgroundImage = "url('https://github.com/ghelix2004/PMST-ETOOL/raw/master/nasa-Q1p7bh3SHj8-unsplash.jpg')";
    }
    else {        document.documentElement.setAttribute('data-theme', 'light');
          localStorage.setItem('theme', 'light');document.body.style.backgroundImage = "url('https://github.com/ghelix2004/PMST-ETOOL/raw/master/larm-rmah-2nPFjsFOuZ4-unsplash.jpg')";
    }    
}

